mapper.select:
SELECT e.emp_idx , e.emp_no , e.emp_name , e.emp_gender , e.department_idx , c.common_code_name , e.team_idx , t.team_name , e.role_idx , r.role_name , e.emp_phone , e.emp_email , e.hire_date , e.emp_status FROM EMPLOYEE_INFO e LEFT JOIN COMMON_CODE c ON e.department_idx = c.common_code_idx LEFT JOIN TEAM t ON e.team_idx = t.team_idx LEFT JOIN ROLE r ON e.role_idx = r.role_idx
WHERE ${searchType} LIKE CONCAT('%', #{searchKeyword}, '%')
ORDER BY ${orderKey} ${orderMethod}
LIMIT #{startRow} , #{listLimit}

mapper.insert:
INSERT INTO EMPLOYEE_INFO
VALUES(
	null
	, #{empNo}
	, #{empName}
	, #{empGender}
	, #{departmentIdx}
	, #{teamIdx}
	, #{roleIdx}
	, #{empPhone}
	, #{empEmail}
	, #{empPassword}
	, #{hireDate}
	, default
	, default
	, default
)

mapper.update:
UPDATE EMPLOYEE_INFO
SET role_idx = NULL
WHERE role_idx = #{roleIdx}

UPDATE EMPLOYEE_INFO
SET team_idx = NULL
WHERE team_idx = #{teamIdx}
