mapper.insert:
INSERT INTO employee_info
VALUES(
	null
	, #{empNo}
	, #{empName}
	, #{empGender}
	, COALESCE(#{departmentIdx}, 1)
	, COALESCE(#{teamIdx}, 1)
	, COALESCE(#{roleIdx}, 1)
	, #{empPhone}
	, #{empEmail}
	, #{empId}
	, #{empPassword}
	, #{hireDate}
	, default
	, default
	, default
)

mapper.update:
UPDATE employee_info
SET role_idx = NULL
WHERE role_idx = #{roleIdx}

UPDATE employee_info
SET team_idx = NULL
WHERE team_idx = #{teamIdx}
