mapper.select:
SELECT idx
from TEAM
where department_idx = #{departmentIdx}

SELECT role_idx
from ROLE
where department_idx = #{departmentIdx}

mapper.update:
UPDATE COMMON_CODE 
SET common_code_name = #{departmentName}
where common_code_idx = #{departmentIdx}

UPDATE TEAM
SET team_name = #{teamName}
where team_idx = #{teamIdx}

UPDATE ROLE
SET role_name = #{roleName}
where role_idx = #{roleIdx}
