mapper.select:
SELECT s.supplier_idx, s.supplier_name, s.supplier_manager, s.supplier_manager_phone, s.supplier_zipcode, s.supplier_address1, s.supplier_address2, s.status FROM SUPPLIER s
status = #{searchType}
AND supplier_name LIKE CONCAT('%', #{searchKeyword}, '%')
ORDER BY ${orderKey} ${orderMethod}
LIMIT #{startRow} , #{listLimit}

SELECT COUNT(*) FROM SUPPLIER
status = #{searchType}
AND supplier_name LIKE CONCAT('%', #{searchKeyword}, '%')

mapper.insert:
INSERT INTO SUPPLIER
	(supplier_name
	, supplier_manager
	, supplier_manager_phone
	, supplier_zipcode
	, supplier_address1
	, supplier_address2)
VALUES
	(#{supplierName}
	, #{supplierManager}
	, #{supplierManagerPhone}
	, #{supplierZipcode}
	, #{supplierAddress1}
	, #{supplierAddress2})

mapper.update:
UPDATE SUPPLIER
SET
	supplier_name = #{supplierName}
	, supplier_manager = #{supplierManager}
	, supplier_manager_phone = #{supplierManagerPhone}
	, supplier_zipcode = #{supplierZipcode}
	, supplier_address1 = #{supplierAddress1}
    , supplier_address2 = #{supplierAddress2}
    , status = #{status}
WHERE supplier_idx = #{supplierIdx}
