mapper.select:
SELECT spc.supply_contract_idx , spc.contract_price , spc.contract_start , spc.contract_end , spc.min_order_quantity , spc.max_order_quantity , spc.status , spc.note , s.supplier_idx , s.supplier_name , s.supplier_manager , s.supplier_manager_phone , s.status as supplier_status , p.product_idx , p.product_name , p.product_volume , p.product_from , c.common_code_name as category_name , p.status as product_status , f.file_idx FROM SUPPLIER_PRODUCT_CONTRACT spc LEFT JOIN SUPPLIER s on spc.supplier_idx = s.supplier_idx LEFT JOIN PRODUCT p on spc.product_idx = p.product_idx LEFT JOIN FILE f on f.target_table = 'product' AND f.target_table_idx = p.product_idx LEFT JOIN COMMON_CODE c on c.common_code_idx = p.category_idx
${searchType} LIKE CONCAT('%', #{searchKeyword}, '%')
AND spc.status = #{filterStatus}
ORDER BY ${orderKey} ${orderMethod}
LIMIT #{startRow} , #{listLimit}

SELECT count(*) FROM SUPPLIER_PRODUCT_CONTRACT spc LEFT JOIN SUPPLIER s on spc.supplier_idx = s.supplier_idx LEFT JOIN PRODUCT p on spc.product_idx = p.product_idx LEFT JOIN FILE f on f.target_table = 'product' AND f.target_table_idx = p.product_idx LEFT JOIN COMMON_CODE c on c.common_code_idx = p.category_idx
${searchType} LIKE CONCAT('%', #{searchKeyword}, '%')
AND spc.status = #{filterStatus}
