mapper.insert:
INSERT
INTO FILE(
	idx,
	original_file_name,
	real_file_name, 
	sub_dir, 
	file_size, 
	content_type, 
	upload_date	)
VALUES

	(
		#{file.idx}
		, #{file.originalFileName}
		, #{file.realFileName}
		, #{file.subDir}
		, #{file.fileSize}
		, #{file.contentType}
		, now()
	)

