mapper.insert:
	INSERT INTO DISPATCH (
		dispatch_date
        , start_slot
        , region_idx
        , created_at
        , status
        , urgent
        , requires_additional
	)
	VALUES (
		#{dispatchDate}
		, #{startSlot}
		, #{regionIdx}
		, NOW()
		, #{status}
		, #{urgent}
		, #{requiresAdditional}
	)

INSERT INTO DISPATCH_ASSIGNMENT (
	dispatch_idx
	, driver_idx
	, vehicle_idx
	, status
	, created_at
)
VALUES (
	#{dispatchIdx}
	, #{empIdx}
	, #{vehicleIdx}
	, #{status}
	, NOW()
)

INSERT INTO DISPATCH_ORDER_MAP (
	dispatch_idx
	, outbound_order_idx
	, created_at
)
VALUES (
	#{dispatchIdx}
	, #{outboundOrderIdx}
	, NOW()
)
