mapper.insert:
INSERT INTO ROUTE(region_idx, delivery_sequence, franchise_idx)
VALUES (
	#{regionIdx}
	, #{deliverySequence}
	, #{franchiseIdx}
)

mapper.delete:
DELETE FROM ROUTE
WHERE
	region_idx = #{regionIdx}
AND 
	franchise_idx = #{franchiseIdx}

mapper.update:
UPDATE
	ROUTE
SET
	delivery_sequence = #{deliverySequence}
WHERE
	region_idx = #{regionIdx}
AND
	franchise_idx = #{franchiseIdx}
